const t={key:"v-5b9f08b4",path:"/postslist/vis/",title:"",lang:"zh-CN",frontmatter:{layout:"ClassificationLayout",classification:"vis"},excerpt:"",headers:[],filePathRelative:null,git:{},postsList:{posts:[{title:"绘出自己的数字画像",path:"/vis/draw-your-own-data-portrait.html",pathRelative:"vis/draw-your-own-data-portrait.html",filePathRelative:"vis/draw-your-own-data-portrait.md",tags:["data-vis"],createdTime:162599744e4,updatedTime:162599744e4,date:null,summary:"现在很多软件和网站都会记录用户的使用行为，分析这些数据可以对用户进行更精准的定位，便于进行更精准的广告投放或个性化信息推送，这样的过程称为绘制用户画像，不过这里说的画像不是一副真实可见的「肖像画」，更多的是基于数据对用户进行定性分类和语言描述。或许我们可以借鉴这样的方法，从自我量化的角度，在日常记录自己相关的数据，绘制自己的数字画像。",collection:"",collectionOrder:0,cover:"data-portrait.jpg"},{title:"数据可视化学习路径及经验",path:"/vis/learning-data-visualization.html",pathRelative:"vis/learning-data-visualization.html",filePathRelative:"vis/learning-data-visualization.md",tags:["data-vis"],createdTime:1625588049e3,updatedTime:162599744e4,date:null,summary:"数据可视化已经发展为一个专业领域，其中还有不少细分方向，如有的工作专注于科学数据可视化，另一些则热衷于生产艺术，不同的领域有不同的学习路径和经验，该博文摘录数据可视化从业者或学习者的经验，以供学习借鉴。",collection:"",collectionOrder:0,cover:"data-vis.jpg"}],tags:["data-vis"]}};export{t as data};
